package org.barend.babelmagic

import org.junit.Assert
import org.junit.Test
import java.io.File
import java.util.*

class AzureTextToSpeechUnitTest
{
    @Test
    fun test()
    {
        val info = createServiceCallInfo()
        if (!info.enabled)
        {
            return
        }

        val answer = callTextToSpeech(info, "en", "Test speech")
        val adapted = adaptJson(answer)
        println("Answer: ${answer}\nAdapted: ${adapted}")
        val msg = "Wrong answer: ${answer}"
        Assert.assertFalse(msg, adapted.contains("Exception"))
        Assert.assertTrue(msg, adapted.contains(",code:"))
        Assert.assertTrue(msg, adapted.contains(",voice:"))
        Assert.assertTrue(msg, adapted.contains(",bytes:"))
    }

    @Test
    fun testTextEncoding()
    {
        val info = createServiceCallInfo(encrypt = true)
        if (!info.enabled)
        {
            return
        }

        val answer = callTextToSpeech(info, "en", "Test \"speech\" with quotes.")
        val adapted = adaptJson(answer)
        println("Answer: ${answer}\nAdapted: ${adapted}")
        val msg = "Wrong answer: ${answer}"
        Assert.assertFalse(msg, adapted.contains("Exception"))
        Assert.assertTrue(msg, adapted.contains(",code:"))
        Assert.assertTrue(msg, adapted.contains(",voice:"))
        Assert.assertTrue(msg, adapted.contains(",bytes:"))
    }

    @Test
    fun testTextToSpeechParse()
    {
        // The answer of the test above is pasted here.
        // It can be saved to verify that the MP3 was generated, encoded, sent as json from Azure to Kotlin, decoded, and playable.
        val decoder = Base64.getDecoder()
        val bytes = decoder.decode(
            ""
        )
        Assert.assertEquals(28800, bytes.size)
        if (false)
        {
            val file = File("/tmp/test_speech.mp3")
            file.writeBytes(bytes)
        }
    }

    @Test
    fun testErrorRequest()
    {
        val info = createServiceCallInfo()
        if (!info.enabled)
        {
            return
        }
        val answer = callTextToSpeech(info, "en", "")
        println(answer)
        val msg = "Wrong answer: ${answer}"
        Assert.assertTrue(msg, answer.contains("error"))
        Assert.assertTrue(msg, answer.contains("Pass parameters"))
    }

    @Test
    fun testErrorTime()
    {
        val info = createServiceCallInfo(timeOffset =  10000)
        if (!info.enabled)
        {
            return
        }
        val answer = callTextToSpeech(info, "en", "Test speech")
        println(answer)
        val msg = "Wrong answer: ${answer}"
        Assert.assertTrue(msg, answer.contains("error"))
        Assert.assertTrue(msg, answer.contains("Time elapsed"))
    }

    @Test
    fun testErrorLanguageCode()
    {
        val info = createServiceCallInfo()
        if (!info.enabled)
        {
            return
        }
        val answer = callTextToSpeech(info, "XX", "Test speech")
        println(answer)
        val msg = "Wrong answer: ${answer}"
        Assert.assertTrue(msg, answer.contains("error"))
        Assert.assertTrue(msg, answer.contains("Language"))
        Assert.assertTrue(msg, answer.contains("not supported"))
    }
}